let message = "✨Danh sách điểm thưởng của người dùng✨\n\n";

for (let i = 0; i < items.length; i++) {
  const user = items[i].json;
  message += `👤 ${user.username} - ${user.telegramid}: ${user.point} BBO\n`;
}

return [{ json: { text: message } }];

const newProposals = $items("GetProposals1", 0).map(item => item.json);
const oldProposals = $items("CodeConvert", 0).map(item => item.json);

const oldIds = oldProposals.map(p => p.proposal_id);
const newOnly = newProposals.filter(p => !oldIds.includes(p.proposal_id));

// Log thử để kiểm tra
console.log("oldIds", oldIds);
console.log("newProposalIds", newProposals.map(p => p.proposal_id));
console.log("newOnly", newOnly);

const mergedProposals = [...oldProposals, ...newOnly];

return [
  {
    json: {
      newProposals: newOnly,
      mergedProposals
    }
  }
];

const items = $input.all();
const result = items.map(item => {
  return {
    json: {
      chat_id: item.json.telegramid.toString()
    }
  };
});
return result;

const rawText = $json.content002;
let parsedData = [];

try {
  parsedData = JSON.parse(rawText);
} catch (e) {
  throw new Error("Không thể parse JSON: " + e.message);
}

return parsedData.map(item => ({ json: item }));

const proposals = $json.newProposals;

const message = proposals.map((p, i) => {
  const escape = (str) => str?.toString().replace(/[_*[\]()~`>#+\-=|{}.!]/g, '\\$&') || 'Không có';

  return (
    `*📢 Đề xuất mới #${i + 1}:*\n` +
    `*🔹 ID:* ${escape(p.proposal_id)}\n` +
    `*🔹 Loại:* ${escape(p.proposal_type)}\n` +
    `*🔹 Tiền đặt cọc:* ${escape(p.deposit)}\n` +
    `*⏳ Hết hạn:* ${escape(p.expiration)}\n` +
    `*📝 Tiêu đề:* ${escape(p.meta_json?.body?.title)}\n` +
    `*📄 Mô tả:* ${escape(p.meta_json?.body?.abstract)}\n`
  );
}).join('\n──────────────\n');

return [{ json: { message } }];

const date = new Date($('Schedule Trigger').item.json.timestamp);
const day = String(date.getDate()).padStart(2, '0');
const month = String(date.getMonth() + 1).padStart(2, '0'); // Tháng bắt đầu từ 0
const year = date.getFullYear();

const formattedDate = `${day}-${month}-${year}`;

return [{
  json: {
    ...$json,
    formattedDate
  }
}];

